# ecom/manage.pyimport osimport sysdef main():    os.environ.setdefault('DJANGO_SETTINGS_MODULE', 'sanruum.settings')    try:        from django.core.management import execute_from_command_line        from django.core.management.commands.runserver import Command as RunserverCommand        # Check if 'runsslserver' is in the command-line arguments        if 'runsslserver' in sys.argv:            try:                from apps.support.management.commands.runsslserver import Command as SSLServerCommand                # Replace the default RunserverCommand with SSLServerCommand                RunserverCommand = SSLServerCommand            except ModuleNotFoundError as e:                print(f"Error: {e}")                print("Ensure the runsslserver command exists and the directory structure is correct.")                sys.exit(1)        execute_from_command_line(sys.argv)    except ImportError as exc:        raise ImportError(            "Couldn't import Django. Are you sure it's installed and "            "available on your PYTHONPATH environment variable? Did you "            "forget to activate a virtual environment?"        ) from excif __name__ == '__main__':    main()